import React, {Component} from 'react';
class Button extends Component{
state = {
   value:"odin"
};

onChange = e =>{

	this.setState({value:e.target.value})
}

render(){
	const {value} = this.state;
	return(

		<form>
        
			<label>
				
				<input type="radio"
 				       value="blue"
                        checked={value==="blue"}
				       onChange={this.onChange}/>
                Blue
			</label>
           
			<label>
				
				<input type="radio"
 				       value="red"
                        checked={value==="red"}
				       onChange={this.onChange}/>
              Rouge
			</label>
           
	
		</form>

)

}

}

export default Button;
