import React, { useState, useContext, useEffect } from 'react'
import { TaskListContext } from '../contexts/TaskListContext'
import uuid from 'uuid';
import axios from "axios"
const TaskForm = (props) => {
  const { addTask, clearList, editTask, editItem } = useContext(TaskListContext)

  const modal=
  { id:"",
    nom:"",
    prenom:"",
    job:""
  }
  //state du champ eleve
  const [title, setTitle] = useState(modal)

  const handleSubmit = e => {
    e.preventDefault()
    //ajouter un eleve
    console.log("sumit",title)
    if (!editItem) {
      addTask(title)
      setTitle({...modal})
    } else {
      editTask({...modal}, editItem.uuid())
    }
  }

  const handleChange = e => {
    e.preventDefault()
    console.log("handle",e.target.value)
    setTitle({...title,
              id:uuid(),
              [e.target.id]:e.target.value
    })
  }



//destructuring des variables
const {nom,prenom,job}=title;
console.log(title.nom)

  return (
    <form onSubmit={handleSubmit} className="form">
      <input
        type="text"
        placeholder="Nom"
        value={nom}
        onChange={handleChange}
        required
        id="nom"
        className="task-input"
      />

            <input
        type="text"
        placeholder="prenom"
        value={prenom}
        onChange={handleChange}
        required
        id="prenom"
        className="task-input"
      />
       <input
        type="text"
        placeholder="job"
        value={job}
        onChange={handleChange}
        required
        id="job"
        className="task-input"
      />
      <div className="buttons">
        <button type="submit" className="btn add-task-btn">
          {editItem ? 'Edit Tas' : 'Ajouter'}
        </button>
        <button className="btn clear-btn" onClick={clearList}>
          supprimer
        </button>
      </div>
    </form>
  )
}

export default TaskForm
